mapper.update:
UPDATE product
prod_name = #{prodName:VARCHAR}, prod_detail = #{prodDetail:VARCHAR}, manufacture_day = #{manuDate:VARCHAR}, price = #{price:INTEGER}, image_file = #{fileName:VARCHAR}, amount = #{amount:INTEGER},
prod_no = #{prodNo}

mapper.select:
SELECT * FROM (
	SELECT ROWNUM row_num, vt.* FROM (
		
	) vt
	WHERE ROWNUM <= #{currentPage}*#{pageSize}
)

	row_num >= ((#{currentPage}-1)*#{pageSize}+1)
